package main

import (
	"fmt"
	"os/exec"
)

func main() {
	var encodedCmd string
	encodedCmd = ""
	// 创建一个 CMD 执行  powershell -noP -sta -w 1 -enc
	cmd := exec.Command("cmd", "/c", "powershell", "-noP", "-sta", "-w", "1", "-enc", encodedCmd)

	// 获取用户输入
	var input string
	fmt.Scanln(&input)
	// 运行 cmd
	err := cmd.Run()
	if err != nil {
		// log.Fatal(err)
		fmt.Println(err)
	}
}
